export function addGradFlamIcon(appendTo) {
    // grad icon flame
	appendTo.gradIcoFlam = libCalc.createSVGElement('linearGradient', {
			id: 'gradIcoFlam',
			x1: '0%',
			y1: '0%',
			x2: '0%',
			y2: '100%'
		},appendTo.svgElem
	);
	appendTo.col1_gradIcoFlam = libCalc.createSVGElement('stop', {
			'stop-color': '#f12c06',
			offset: '0'
		},appendTo.gradIcoFlam
	);
	appendTo.col2_gradIcoFlam = libCalc.createSVGElement('stop', {
			'stop-color': '#faed34',
			offset: '1'
		},appendTo.gradIcoFlam
	);
}

export function addGradLeafIcon(appendTo) {
    // grad icon flame
	appendTo.gradLeaf = libCalc.createSVGElement('radialGradient', {
    		id: 'gradIcoLeaf',
			x1: '80%',
			y1: '0%',
			x2: '0%',
			y2: '100%'
    	},appendTo.svgElem
    );
    appendTo.col1_gradLeaf = libCalc.createSVGElement('stop', {
			'stop-color': '#13eb13',
			offset: '0%'
		},appendTo.gradLeaf
	);
	appendTo.col2_gradLeaf = libCalc.createSVGElement('stop', {
			'stop-color': '#1d8d1d',
			offset: '100%'
		},appendTo.gradLeaf
	);
}

